import React, {Component} from 'react';
import logo from "/src/react_interface/airport_user_interface/src/assets/logo.png"
import "./Login.css";


class LoginComponent extends Component {
    constructor(props) {
        super(props);
    }

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(event.target.email.value);

        if (!event.target.email.value) {
            alert("Email is required");
        } else if (!event.target.email.value) {
            alert("Valid email is required");
        } else if (!event.target.password.value) {
            alert("Password is required");
        } else if (
            event.target.email.value === "me@example.com" &&
            event.target.password.value === "123456"
        ) {
            alert("Successfully logged in");
            event.target.email.value = "";
            event.target.password.value = "";
        } else {
            alert("Wrong email or password combination");
        }
    }

    handleClick = (event)=>{
        event.preventDefault()
        alert("Goes to registration page")
    }

    render() {
        return (
            <div className="Login">
                <img src={logo} className="logo" alt="Business view - Reports" />
                <form className="form" onSubmit={this.handleSubmit}>
                    <div className="input-group">
                        <label htmlFor="email">Email</label>
                        <input type="email" name="email" placeholder="nome@email.com.br" />
                    </div>
                    <div className="input-group">
                        <label htmlFor="password">Senha</label>
                        <input type="password" name="password" />
                    </div>
                    <button className="primary">ENTRAR</button>
                </form>
                <button className="secondary" onClick={this.handleClick}>
                    Criar uma nova conta
                </button>
            </div>
        );
    }
}

export default LoginComponent;
