import './App.css';
import {BrowserRouter as Router, Switch, Route, Link} from 'react-router-dom';
import Sidebar from "./components/nav_bars/Sidebar";
import CreateAircraftComponent from "./components/add_components/CreateAircraftComponent";
import CreateCompanyComponent from "./components/add_components/CreateCompanyComponent";
import CreateEmployeeComponent from "./components/add_components/CreateEmployeeComponent";
import CreateFlightComponent from "./components/add_components/CreateFlightComponent";
import MainPage from "./components/main_components/MainPage";
import CreateClientComponent from "./components/add_components/CreateClientComponent";
import SearchEmployeeComponent from "./components/main_components/home/SearchEmployeeComponent";
import SearchFlightComponent from "./components/main_components/home/SearchFlightComponent";
import DepartureFlightComponent from "./components/main_components/flight/DepartureFlightComponent";
import ArrivalFlightsComponent from "./components/main_components/flight/ArrivalFlightsComponent";
import SearchAircraftComponent from "./components/main_components/flight/SearchAircraftComponent";
import SearchCompaniesComponent from "./components/main_components/companies/SearchCompaniesComponent";
import SearchClientsComponent from "./components/main_components/clients/SearchClientsComponent";
import LoginComponent from "./components/main_components/login/LoginComponent";
import {Fragment, useState} from "react";
import {Button} from "react-bootstrap";


function App() {

    const adminUser = {
        email: "test@gmail.com",
        password: "test"
    }
    const [user, setUser] = useState({name: "", email: ""})
    const [error, setError] = useState("")

    const Login = details => {
        console.log(details)

        if (details.email == adminUser.email && details.password == adminUser.password) {
            console.log("Logged in");
            setUser({
                name: details.name,
                email: details.email
            })
        } else {
            console.log("Details do not match")
            setError("Details do not match")
        }
    }
    const Logout = () => {
        console.log("Logout")
        setUser({name: "", email: ""})
    }

    return (
        // <Router>
        //     <Switch>
        //         <Route path="/" exact component={LoginComponent}/>
        //         <Route path='/*' exact component={MainPage}/>
        //     </Switch>
        // </Router>
        <div className="App">
            {(user.email != "") ? (<div className="welcome">
                <h2>Welcome, <span>{user.name}</span></h2>
                <Button onClick={Logout}>Logout</Button>
            </div>) : (<LoginComponent Login={Login} error={error}/>)
            }
        </div>
    );


}


export default App;
