import React, {Component} from 'react';
import logo from "./assets/logo.jpg"
import "./Login.css";
import LoginService from "./services/LoginService";

class LoginComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            insertedUsername: '',
            insertedPassword: ''
        }
    }

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(event.target.email.value);

        this.setState({insertedUsername: event.target.email.value})
        this.setState({insertedPassword: event.target.password.value})


        let loginDto = {
            insertedUsername: this.state.insertedUsername,
            insertedPassword: this.state.insertedPassword
        }

        LoginService.checkUserCredentials(loginDto).then((response => {
            alert(response.data)
        }))

        // if (!event.target.email.value) {
        //     alert("Email is required");
        // } else if (!event.target.email.value) {
        //     alert("Valid email is required");
        // } else if (!event.target.password.value) {
        //     alert("Password is required");
        // } else if (
        //     event.target.email.value === "me@example.com" &&
        //     event.target.password.value === "123456"
        // ) {
        //     alert("Successfully logged in");
        //     event.target.email.value = "";
        //     event.target.password.value = "";
        // } else {
        //     alert("Wrong email or password combination");
        // }
    }

    render() {
        return (
            <div className="Login">
                <img src={logo} className="logo" alt="Logo"/>
                <form className="form" onSubmit={this.handleSubmit}>
                    <div className="input-group">
                        <label htmlFor="email">Username</label>
                        <input type="email" name="email" placeholder="Username here..." value={this.state.insertedUsername}/>
                    </div>
                    <div className="input-group">
                        <label htmlFor="password">Password</label>
                        <input type="password" name="password" value={this.state.password}/>
                    </div>
                    <button className="primary" onClick={this.handleSubmit}>Login</button>
                </form>
            </div>
        );
    }
}

export default LoginComponent;
