import * as React from 'react';
import {useTheme} from '@mui/material/styles';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import FlightServices from "../../../services/FlightServices";

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
    PaperProps: {
        style: {
            maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
            width: 250,
        },
    },
};

const names = [
    'Oliver Hansen',
    'Van Henry',
    'April Tucker',
    'Ralph Hubbard',
    'Omar Alexander',
    'Carlos Abbott',
    'Miriam Wagner',
    'Bradley Wilkerson',
    'Virginia Andrews',
    'Kelly Snyder',
];

function getStyles(name, personName, theme) {
    return {
        fontWeight:
            personName.indexOf(name) === -1
                ? theme.typography.fontWeightRegular
                : theme.typography.fontWeightMedium,
    };
}


export default function MultipleSelect(props) {
    const theme = useTheme();
    const [value, setValue] = React.useState([]);

    const handleChange = (event) => {
        const {
            target: {value},
        } = event;
        setValue(
            // On autofill we get a the stringified value.
            typeof value === 'string' ? value.split(',') : value,
        );
    };

    const btnFunction = (event) => {
        //sent the data back to the main component
        event.preventDefault()
        props.parentCallBack(value)
    }
    return (
        <div className="form-group">
            <FormControl sx={{m: 1, width: 300}}>
                <InputLabel id="demo-multiple-name-label">Name</InputLabel>
                <Select
                    labelId="demo-multiple-name-label"
                    id="demo-multiple-name"
                    multiple
                    value={value}
                    onChange={handleChange}
                    input={<OutlinedInput label="Name"/>}
                    MenuProps={MenuProps}>

                    {props.dataApi.map((row) => (
                        <MenuItem
                            key={row.flight.id}
                            value={row.flight.fromLocation + "->" + row.flight.toLocation}
                            style={getStyles(row.flight.fromLocation + "->" + row.flight.toLocation, value, theme)}>
                            {row.flight.fromLocation + "->" + row.flight.toLocation}
                        </MenuItem>
                    ))}

                </Select>
                <button className="btn btn-success" onClick={btnFunction}>Display flights</button>
            </FormControl>

        </div>
    );
}
