import React, {Component} from 'react';
import { cardShadow, hoverEffect, themeColor } from "./utils";
import OtopeniAvatar from "./utils/otopeniAvatar.jpg"
import styled from "styled-components";

class Projects extends Component {
    render() {
        return (
            <div>
                <YourProjects>
                    <Project>
                        <Avatar>
                            <img src={OtopeniAvatar} alt="" />
                        </Avatar>
                        <Detail>
                            <Title>Company name here</Title>
                            <SubTitle>Number of the employee here</SubTitle>
                        </Detail>
                    </Project>
                    <Project>
                        <Avatar>
                            <img src={OtopeniAvatar} alt="" />
                        </Avatar>
                        <Detail>
                            <Title>Personal branding project</Title>
                            <SubTitle>5 days remaining</SubTitle>
                        </Detail>
                    </Project>
                    <AllProjects>See all projects</AllProjects>
                </YourProjects>
            </div>
        );
    }
}

const YourProjects = styled.div`
  height: 95%;
  background-color: white;
  margin: 0;
  padding: 1rem;
  border-radius: 1rem;
  box-shadow: ${cardShadow};
  transition: 0.4s ease-in-out;
  &:hover {
    box-shadow: ${hoverEffect};
  }
  @media screen and (min-width: 320px) and (max-width: 1080px) {
    height: max-content;
    width: 75%;
    margin-top: 1rem;
  }
`;

const Project = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 0.3rem;
`;
const Avatar = styled.div`
  img {
    height: 4rem;
    width: 4rem;
    border-radius: 4rem;
  }
`;
const Detail = styled.div`
  margin-left: 1rem;
`;
const Title = styled.h5`
  font-weight: 500;
  @media screen and (min-width: 320px) and (max-width: 1080px) {
    font-size: 1rem;
  }
`;
const SubTitle = styled.h6`
  font-weight: 300;
`;
const AllProjects = styled.h6`
  text-align: end;
  color: ${themeColor};
  cursor: pointer;
`;

export default Projects;
