import React, {Component} from 'react';
import {Dropdown} from "react-bootstrap";


class CreateFlightComponent extends Component {

    constructor(props) {
        super(props);


        this.state = {
            departureTime: '',
            arrivalTime: '',
            fromLocation: '',
            toLocation: '',
            aircraft: ''
        }
        this.changeDepartureTime = this.changeDepartureTime.bind(this);
        this.changeArrivingTime = this.changeArrivingTime.bind(this);
        this.changeToLocation = this.changeToLocation.bind(this);
        this.changeFromLocation = this.changeFromLocation.bind(this);
        this.changeAircraft = this.changeAircraft.bind(this);
        this.saveFlight = this.saveFlight.bind(this);
    }

    changeDepartureTime = (event) => {
        this.setState({departureTime: event.target.value})
    }

    changeArrivingTime = (event) => {
        this.setState({arrivalTime: event.target.value})
    }

    changeFromLocation = (event) => {
        this.setState({fromLocation: event.target.value})
    }

    changeToLocation = (event) => {
        this.setState({toLocation: event.target.value})
    }

    changeAircraft = (event) => {
        this.setState({aircraft: event.target.value})
    }

    saveFlight = (event) => {
        event.preventDefault();

        let flight = {
            departureTime: this.state.departureTime,
            arrivalTime: this.state.arrivalTime,
            fromLocation: this.state.fromLocation,
            toLocation: this.state.fromLocation,
            aircraft: this.state.aircraft
        }

        console.log("flight => " + JSON.stringify(flight));
    }

    render() {
        return (
            <div>
                <div className="container">
                    <div className="row" style={{marginTop:"15px"}}>
                        <div className="card col-md-6 offset-md-3 offset-md-3">
                            <h3 className="text-center">Insert Flight</h3>
                            <div className="card-body">
                                <form>
                                    <div className="form-group">
                                        <label>Departure Time</label>
                                        <input placeholder="Departure Time" name="departureTime"
                                               className="form-control"
                                               value={this.state.departureTime} onChange={this.changeDepartureTime}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Arrival Time</label>
                                        <input placeholder="Arrival Time" name="arrivalTime" className="form-control"
                                               value={this.state.arrivalTime} onChange={this.changeArrivingTime}
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label>From Location</label>
                                        <input placeholder="From Location" name="fromLocation" className="form-control"
                                               value={this.state.fromLocation} onChange={this.changeFromLocation}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>To Location</label>
                                        <input placeholder="To Location" name="toLocation" className="form-control"
                                               value={this.state.toLocation} onChange={this.changeToLocation}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Aircraft</label>
                                        <input placeholder="Aircraft" name="aircraft" className="form-control"
                                               value={this.state.aircraft} onChange={this.changeAircraft}
                                        />
                                    </div>

                                    <div className="form-group">
                                    <div className="dropdown">
                                        <button className="btn btn-secondary dropdown-toggle" type="button"
                                                id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true"
                                                aria-expanded="false">
                                            Dropdown button
                                        </button>
                                        <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                                            <a className="dropdown-item" href="#">Action</a>
                                            <a className="dropdown-item" href="#">Another action</a>
                                            <a className="dropdown-item" href="#">Something else here</a>
                                        </div>
                                    </div>
                                    </div>
                                    <button className="btn btn-success" style={{marginTop: "10px"}}
                                            onClick={this.saveFlight}>Save
                                    </button>
                                    <button className="btn btn-danger"
                                            style={{marginLeft: "10px", marginTop: "10px"}}>Cancel
                                    </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default CreateFlightComponent;
