import React, {Component} from 'react';
import {Dropdown} from "react-bootstrap";
import {AiOutlineMore} from "react-icons/ai";

class DropdownUpdateDelete extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: this.props.id
        }
    }

    updateItem = () => {
        this.props.updateItem(this.state.id)
    }

    deleteItem = () => {
        this.props.deleteItem(this.state.id)
    }

    render() {
        return (
            <Dropdown>
                <Dropdown.Toggle variant="" id="dropdown-basic">
                    <AiOutlineMore/>
                </Dropdown.Toggle>
                <Dropdown.Menu>
                    <Dropdown.Item
                        onClick={() => this.update()}>Update</Dropdown.Item>
                    <Dropdown.Item
                        onClick={() => this.save()}>Delete</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
        );
    }
}

export default DropdownUpdateDelete;
